    $scope.saveProfile = function() {
        var FN = $scope.firstname;
        if (FN == "" || FN == null) {
            navigator.notification.alert("Please enter your first name", null, 'First Name Required', 'Ok');
            return;
        }
        var LN = $scope.lastname;
        if (LN == "" || LN == null) {
            navigator.notification.alert("Please enter your last name", null, 'Last Name Required', 'Ok');
            return;
        }
        var userRollNo = $scope.userRollNo;
        if (userRollNo == "" || userRollNo == null) {
            navigator.notification.alert("Please enter your roll number", null, 'Roll Number Required', 'Ok');
            return;
        }
        var userPhnNum = $scope.PhnNumber;
        if (userPhnNum == "" || userPhnNum == null) {
            navigator.notification.alert("Please enter your phone number", null, 'Phone Number Required', 'Ok');
            return;
        }
        var userRole = $scope.userRole;
        if (userRole == "" || userRole == null) {
            navigator.notification.alert("Please select your role", null, 'Role Required', 'Ok');
            return;
        }

        var str = userPhnNum;
        if(/^[a-zA-Z0-9- ]*$/.test(str) == false) {
                navigator.notification.alert("Invalid Phone number", null, 'Incorrect Phone Number', 'Ok');
            return;
        }
        

        var userData = {};
        var token = $.jStorage.get("userdetails").usertoken;
        var _id = $.jStorage.get("userdetails")._id;
        var tenant = $rootScope.metadata.tenantid;

        userData.usertoken = token;
        userData._id = _id;
        userData.tenant = tenant;
        userData.rollno = $scope.userRollNo;

        userData.firstname = FN;
        userData.lastname = LN;

        userData.email = $scope.email;

        userData.studentRole = $scope.userRole;
        userData.phone = $scope.PhnNumber;

        userData.userRole = $scope.userRole;



        var url = "https://kryptosda.kryptosmobile.com/kryptosds/nuser/updateuserprofile";
        var tenantid = $rootScope.metadata.tenantid;


        $http.post(url, userData).success(function(res) {
            $.unblockUI();
            if (res.success) {
                $rootScope.toggle("confirmUserInfo");
                $.jStorage.set("userdetails", res.userinfo);
                var profiledata = res.userinfo;
                $rootScope.userProfileName = profiledata.firstname + " " + profiledata.lastname;
                $rootScope.useremail = profiledata.email;
                $rootScope.userProfilePic = 'images/profileImage.png';
                $rootScope.showUploadPhoto = true;

                if (profiledata.studentRole == '' || profiledata.studentRole == undefined) {
                    $rootScope.studentRole = "Role";
                } else {
                    $rootScope.studentRole = profiledata.studentRole;
                }

                $rootScope.phone = profiledata.phone;
                $rootScope.rollNo = profiledata.rollno;


                var UP = FN + " " + LN;
                $scope.loopinUserRegister($scope.email, UP, $scope.userRollNo, $scope.PhnNumber, $scope.pwd, "Ideal Institute Of Management And Technology And School Of Law");

            } else {
                if (window.device) {
                    navigator.notification.alert(res.error, null, 'Error', 'Ok');
                } else {
                    alert(res.error);
                }
            }

        }).error(function(err) {
            $.unblockUI();
            alert(JSON.stringify(err));
        });

    }

