        "restaurant_id": restaurantId,
        "time": String(time),
        "date": today,
        "user_id": user_id, "offer_id": "999"
    };
    $http({
        url: postUrl,
        method: 'POST',
        data: postData
    }).then(function (res) {
        // alert(JSON.stringify(res.data.restaurantOfferList));

        $scope.liveoffer = res.data.restaurantOfferList[0];
        $scope.restOffer = $scope.liveoffer.restaurant_offer;
        $scope.name = res.data.restaurant_name;


        //display status only for live deal
        $scope.getCodeTextLive = res.data.restaurantOfferList[0].coupon_code;


        $scope.area = res.data.restaurant_area;
        $scope.image = res.data.restaurant_image;
        $scope.address1 = res.data.restaurant_address1;
        $scope.address2 = res.data.restaurant_address2;
        $scope.contact = res.data.restaurant_contact_detail;
        $scope.endTimeDeal = res.data.restaurantOfferList[0].end_time;
        try {
            if (res.data.restaurantOfferList.length > 1) {
                $scope.upcomingoffer = res.data.restaurantOfferList[1];
                $scope.hideGetCode = false;
                $scope.getCodeTextUpcoming = res.data.restaurantOfferList[1].coupon_code;
            }
            else {
                $scope.hideGetCode = true;

            }

        }
        catch (e) {

        }
        $scope.$apply();
        /*
         setInterval(timeRemaining, 1000);
         */
        $scope.km = $rootScope.km;
        $.unblockUI();
    }, function (err) {
        $scope.err = err;
        $.unblockUI();
    });
    function timeRemaining() {

        var nowTime = new Date();
        var time = $scope.endTimeDeal;
        if (time.indexOf("PM"))
            var endTime = new Date(nowTime.getFullYear(), nowTime.getMonth(), nowTime.getDate(), parseInt(time.slice(0, 2)) + 12, 0, 0, 0);
        else
            var endTime = new Date(nowTime.getFullYear(), nowTime.getMonth(), nowTime.getDate(), parseInt(time.slice(0, 2)), 0, 0, 0);

        var diff = endTime.getTime() - nowTime.getTime();

        var second = (diff / 1000) % 60;

        var minutes = ((diff / (1000 * 60)) % 60);

        var hours = ((diff / (1000 * 60 * 60)) % 24);
        $scope.timeRemainingToEnd = Math.floor(hours) + "h:" + Math.floor(minutes) + "m:" + Math.floor(second) + "s";
        $scope.$apply();

    }

    $scope.openMap = function () {
        var url = "https://maps.google.com?saddr=" + $rootScope.userLat + "," + $rootScope.userLong + "&daddr=" + $rootScope.restLat + "," + $rootScope.restLong;

        window.open(url, "_blank", "location=yes,hidden=no,enableViewportScale=yes");
    }

    $scope.selectDeal = function (deal, flag) {
        if (flag) {
            //confirming a live deal
            if ($scope.getCodeTextLive.indexOf("GET CODE") != -1)
                confirmDeal(deal, flag);
            $.blockUI({
                message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
            });
        }


        else {
            //confirming an upcoming deal
            if ($scope.getCodeTextUpcoming.indexOf("GET CODE") != -1) {
                confirmDeal(deal, flag);
                $.blockUI({
                    message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
                });
            }
        }



    }
    $scope.setRestOffer = function (flag) {
        // alert(flag);
        if (flag) {
            $scope.restOffer = $scope.liveoffer.restaurant_offer;

        }
        else {
            try {
                $scope.restOffer = $scope.upcomingoffer.restaurant_offer;


            }
            catch (e) {

                $scope.restOffer = "No Upcoming Deal";
                $scope.hideGetCode = true;

            }

        }
        $scope.$apply();
    }
    function confirmDeal(deal, flag) {

        //alert(deal.offer_id);
        var profiledata = $.jStorage.get("userdetails");


        var postUrl = "https://dealmonk.in/book_restaurant_kryptos/";
        var postData = {
            "mobile": profiledata.phone,
            "email": profiledata.email,
            "first_name": profiledata.firstname,
            "last_name": profiledata.lastname,
            //"user_id": "1654",
            "restaurantId": restaurantId,
            "offer_id": deal.offer_id,
            "covers": 1,
            "booking_time": String(time),
            "booking_date": today
        };
        $http({
            url: postUrl,
            method: 'POST',
            data: postData
        }).then(function (res) {
            if (res.data.success) {
                if (window.device) {
                    navigator.notification.alert("Cheers!" + "\n" + "Your Deal is Confirmed" + "\n" + res.data.coupon_code);
                }
                else {
                    alert("Cheers!" + "\n" + "Your Deal is Confirmed" + "\n" + res.data.coupon_code);
                }
                $rootScope.GetCode = true;
                if (flag) {
                    $scope.getCodeTextLive = res.data.coupon_code;

                }
                else {
                    $scope.getCodeTextUpcoming = res.data.coupon_code;
                }
                $rootScope.ShowDealDesc = true;
                $rootScope.toggle("replyThreadModal");

            }
            else {
                if (window.device) {
                    navigator.notification.alert(res.data.message);

                }
                else {
                    alert(res.data.message);
                }
            }
            $.unblockUI();
        }, function (err) {
            $scope.err = err;
            $.unblockUI();
        });

    }


}

