            //alert(currentLocation.latitude);" ,long:"-80.128108
            //var start = new google.maps.LatLng(36.106139,-86.799758);
            var start = new google.maps.LatLng($rootScope.placeCoordinate.coords.latitude, $rootScope.placeCoordinate.coords.longitude);
            var end = new google.maps.LatLng($rootScope.place.result.geometry.location.lat, $rootScope.place.result.geometry.location.lng);
            var travelModeOption = "DRIVING";
            switch (travelModeOption) {
                case "walk":
                    {
                        var usertravelMode = "WALKING";
                        var request = {
                            origin: start,
                            destination: end,
                            travelMode: google.maps.TravelMode.WALKING,
                            unitSystem: google.maps.UnitSystem.METRIC
                        };
                        break;
                    }
                case "cycle":
                    {
                        var usertravelMode = "BICYCLING";
                        var request = {
                            origin: start,
                            destination: end,
                            travelMode: google.maps.TravelMode.BICYCLING,
