                    $("#PlacedirectionInfo").html("TravelMode: " + usertravelMode + "<br />Distance = <span style='white-space:nowrap; text-overflow:ellipsis; overflow:hidden;width:35px;'>" + d.slice(0, 6) + "</span> miles <br />Time = " + JSON.stringify(result.routes[0].legs[0].duration.text));
                } catch (e) {}
                directionsDisplay.setDirections(result);
                var leg = result.routes[0].legs[0];
                //PlacemakeMarker(leg.start_location, icons.start, "title");
                var dirMarkerst = new google.maps.Marker({
                                       animation: google.maps.Animation.null,
                                       //draggable:true,
